#' }
#'
#' @export
zmed <- function(
    calculate = "selectpower",
    stdinput = FALSE,
    a = 0.25,
    b = 0,
    cp = 0,
    sdy = 1,
    sdx = 1,
    sdm = 1,
    r2y = 0,
    r2m = 0,
    power = 0.8,
    alpha = 0.05,
    alternative = "not equal",
    mc = FALSE,
    n = 200) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("zmed requires jmvcore to be installed (restart may be required)")


    options <- zmedOptions$new(
        calculate = calculate,
        stdinput = stdinput,
        a = a,
        b = b,
        cp = cp,
        sdy = sdy,
        sdx = sdx,
        sdm = sdm,
        r2y = r2y,
        r2m = r2m,
        power = power,
        alpha = alpha,
        alternative = alternative,
        mc = mc,
        n = n)

    analysis <- zmedClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

